import React, { useEffect, useState } from "react"
import { Table, Thead, Tbody, Tr, Th, Td } from 'react-super-responsive-table';
import 'react-super-responsive-table/dist/SuperResponsiveTableStyle.css';


function UserSelectionForm(props) {
    console.log(props.crime, props.force);

    submitStopandSearch(props.force);
    function submitStopandSearch(force){
        setLoadingStop(true);
        fetch(`https://data.police.uk/api/stops-no-location?force=${force}`)
          .then(response => {
            return response.json()
          })
          .then(data => {
            setStopSearch(data)
            setLoadingStop(false);
          })
      }

      function submit(e)
      {
          setLoadingCrimeAndForce(true);
          e.preventDefault()
          fetch(`https://data.police.uk/api/crimes-no-location?category=${props.crime}&force=${props.force}`)
          .then(response => {
              return response.json()
          })
          .then(data => {
              setNeighbourhoods(data)
              setLoadingCrimeAndForce(false)
          })
      }



   // const [stateForceCrime, setstateForceCrime] = useState({force: "", crime: ""});
   // const [crimeCategorties, setCrimeCategorties] = useState([])
    const [neighbourhoods, setNeighbourhoods] = useState([])
    const [stopSearch, setStopSearch] = useState([])
    const [isLoadingStop, setLoadingStop] = useState(false);
    // const [isLoadingForce, setLoadingForce] = useState(false);
    // const [isLoadingCrime, setLoadingCrime] = useState(false);
    const [isLoadingCrimeAndForce, setLoadingCrimeAndForce] = useState(false);

  return (

    <>
    <Table sTriped bordered hover>
          <Thead>
              <Tr>
                  <Th>ID</Th>
                  <Th>Category</Th>
                  <Th>MonTh</Th>
              </Tr>
          </Thead>
          <Tbody>
              {!isLoadingStop ? <> {isLoadingCrimeAndForce.lengTh ?
                  neighbourhoods.map(neighbourhood => (
                      <Tr>
                          <Td>{neighbourhood.id}</Td>
                          <Td>{neighbourhood.category}</Td>
                          <Td>{neighbourhood.monTh}</Td>
                      </Tr>
                  )) : <p>data not found</p>} </> : <p></p>}
          </Tbody>
      </Table><Table sTriped bordered hover>
              <Thead>
                  <Tr>
                      <Th>age range</Th>
                      <Th>outcome</Th>
                      <Th>self_defined_eThnicity</Th>
                      <Th>gender</Th>
                      <Th>legislation</Th>
                      <Th>officer_defined_eThnicity</Th>
                      <Th>type</Th>
                      <Th>object_of_search</Th>
                  </Tr>
              </Thead>
              <Tbody>
                  {!isLoadingStop ? <> {stopSearch.lengTh ?
                      stopSearch.map(stopSearchs => (
                          <Tr>
                              <Td>{stopSearchs.age_range}</Td>
                              <Td>{stopSearchs.outcome}</Td>
                              <Td>{stopSearchs.self_defined_eThnicity}</Td>
                              <Td>{stopSearchs.gender}</Td>
                              <Td>{stopSearchs.legislation}</Td>
                              <Td>{stopSearchs.officer_defined_eThnicity}</Td>
                              <Td>{stopSearchs.type}</Td>
                              <Td>{stopSearchs.object_of_search}</Td>
                          </Tr>
                      )) : <p>data not found</p>} </> : <p></p>}
              </Tbody>
          </Table></>

  );
}
export default UserSelectionForm;
