import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Test from './components/test'
import React, { useEffect, useState } from "react"
import Form from 'react-bootstrap/Form';

function App() {

    const [stateForceCrime, setstateForceCrime] = useState({force: "", crime: "",stopandsearch: ""});
    const [crimeCategorties, setCrimeCategorties] = useState([])

  // function handle(e)
  // {
  //     const data = {...stateForceCrime}
  //     data[e.target.id] = e.target.value
  //     setstateForceCrime(data)
  // }
  const [state, setstate] = useState({force: "", crime: "",stopandsearch: ""})
  // const changeState = () => {  
  //   setstate({data: }); 
  //  }; 

   const [value, setValue] = useState(getInitialState);

   const handleChange = (e) => {
     setValue(e.target.value);
   };

  const [force, setForce] = useState([])
  const policeForces = () => {
    fetch("https://data.police.uk/api/forces")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setForce(data)
      })
  }
  const policeDataCrimeCategorties = () => {
  fetch("https://data.police.uk/api/crime-categories")
    .then(response => {
      return response.json()
    })
    .then(data => {
      setCrimeCategorties(data)
    })
  }

  useEffect(() => {
    policeDataCrimeCategorties();
    policeForces();
  }, [])  

  return (
    <><div className="App">
      <div className="auth-wrapper">
        <div className="auth-inner">

          <Form>
            <h3>Sign In</h3>
            <div className="mb-3">
              <Form.Group>
                <Form.Label>Select a Police Force</Form.Label>
                <Form.Select>
                  <option value="Non Selected">Non Selected</option>
                  {force.map(force => (
                    <option value={force.id}>{force.id}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </div>
            <div className="mb-3">
              <Form.Group>
                <Form.Label>Select a Crime</Form.Label>
                <Form.Select>
                  <option value="Non Selected">Non Selected</option>
                  {crimeCategorties.map(crimeCategorties => (
                    <option value={crimeCategorties.url}>{crimeCategorties.url}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </div>
            <div className="mb-3">
            <Form.Group>
                <Form.Label>Select a place for stop and search</Form.Label>
                <Form.Select onChange={handleChange}>
                  <option value="Non Selected">Non Selected</option>
                  {force.map(force => (
                    <option value={force.id}>{force.id}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </div>
            <div className="d-grid">
              <button onClick={changeState} className="btn btn-primary">
                Submit
              </button>
            </div>
          </Form>
        </div>
      </div>
    </div>
    <Test force={stateForceCrime.force} crime={stateForceCrime.crime} stopandsearch={stateForceCrime.stopandsearch}></Test>
    </>
   );
}

export default App;
