import Form from 'react-bootstrap/Form';
import React, { useEffect, useState } from "react"
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';


function UserSelectionForm() {

    const [stateForceCrime, setstateForceCrime] = useState({force: "", crime: ""});


    const [stateCrime, setStateCrime] = useState([]);
    const handleChangeCrime = (e) => {
      setStateCrime(e.target.value);
    };


    // function submit(e)
    // {

    // }

    function handle(e)
    {
        const data = {...stateForceCrime}
        data[e.target.id] = e.target.value
        setstateForceCrime(data)
        console.log(data)
    }

    const [crimeCategorties, setCrimeCategorties] = useState([])
    const policeDataCrimeCategorties = () => {
      fetch("https://data.police.uk/api/crime-categories")
        .then(response => {
          return response.json()
        })
        .then(data => {
          setCrimeCategorties(data)
        })
    }



    const [force, setForce] = useState([])
    const policeForces = () => {
      fetch("https://data.police.uk/api/forces")
        .then(response => {
          return response.json()
        })
        .then(data => {
          setForce(data)
        })
    }

    const [neighbourhoods, setNeighbourhoods] = useState([])
    const policeData = (stateCrime,stateForces) => {
        console.log("what im looking for")
        console.log(stateCrime,stateForces);
        console.log(neighbourhoods);

        fetch(`https://data.police.uk/api/crimes-no-location?category=${stateCrime}&force=${stateForces}`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            setNeighbourhoods(data)
        })
    }


    useEffect(() => {
      policeDataCrimeCategorties();
      policeForces();
      policeData();
    }, [])




  return (
      <><Form.Group className="mb-3" onSubmit={policeData(stateCrime,stateForces)}>
          <Form.Label>Select a Crime</Form.Label>
          <Form.Select onChange={(e) => handle(e)}>
          <p>{`You selected ${stateCrime}`}</p>
              {force.map(force => (
                  <option value={force.id}>{force.name}</option>
              ))}
          </Form.Select>
          <Form.Label>Select a Police Force</Form.Label>
          <Form.Select onChange={(e) => handle(e)}>
          <p>{`You selected ${stateForces}`}</p>
              {crimeCategorties.map(crimeCategorties => (
                  <option value={crimeCategorties.name}>{crimeCategorties.name}</option>
              ))}
          </Form.Select>
          <Button type="submit">Submit</Button>
      </Form.Group>
      <div>

      </div>
      <Table striped bordered hover>
              <thead>
                  <tr>
                      <th>ID</th>
                      <th>Category</th>
                      <th>Month</th>
                  </tr>
              </thead>
              <tbody>
                  {neighbourhoods.map(neighbourhood => (
                      <tr>
                          <td>{neighbourhood.id}</td>
                          <td>{neighbourhood.category}</td>
                          <td>{neighbourhood.month}</td>
                      </tr>
                  ))}
              </tbody>
          </Table>
          </>
  );
}
export default UserSelectionForm;
