import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
//import Form from './components/form';
import Component2 from './components/form'
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import React, { useEffect, useState } from "react"
import Button from 'react-bootstrap/Button';

function App() {

  const [stateForceCrime, setstateForceCrime] = useState({force: "", crime: ""});
  const [crimeCategorties, setCrimeCategorties] = useState([])


  const [state, setstate] = useState({data:""})

  const changeState = () => {  
      setstate({force}); 
     }; 

  function handle(e)
  {
      const data = {...stateForceCrime}
      data[e.target.id] = e.target.value
      setstateForceCrime(data)
  }

  const policeDataCrimeCategorties = () => {
      setLoadingCrime(true);
    fetch("https://data.police.uk/api/crime-categories")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setCrimeCategorties(data)
        setLoadingCrime(false);
      })
  }

  const [force, setForce] = useState([])
  const policeForces = () => {
  setLoadingForce(true);
    fetch("https://data.police.uk/api/forces")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setForce(data)
        setLoadingForce(false);
      })
  }

  useEffect(() => {
    policeDataCrimeCategorties();
    policeForces();
  }, [])       

  return (
    <><div className="App">
      <div className="auth-wrapper">
        <div className="auth-inner">
            <Form>
              <div className="mb-3">
                <Form.Group>
                  <Form.Label>Select a Police Force</Form.Label>
                  <Form.Select id="force" onChange={(e) => handle(e)}>
                    <option value="Non Selected">Non Selected</option>
                    {force.map(force => (
                      <option value={force.id}>{force.id}</option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </div>
              <div className="mb-3">
                <Form.Group>
                  <Form.Label>Select a Crime</Form.Label>
                  <Form.Select id="crime" onChange={(e) => handle(e)}>
                    <option value="Non Selected">Non Selected</option>
                    {crimeCategorties.map(crimeCategorties => (
                      <option value={crimeCategorties.url}>{crimeCategorties.url}</option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </div>
              <div className="d-grid">
                <Button type="submit">Search for Specific Crimes in Locations</Button>
              </div>
            </Form>

            <Form>
              <div className="mb-3">
                <Form.Group>
                  <Form.Label>Select a Police Force for stop and Search</Form.Label>
                  <Form.Select id="force" onChange={(e) => handle(e)}>
                    <option value="Non Selected">Non Selected</option>

                    {force.map(force => (
                      <option value={force.id}>{force.id}</option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </div>
              <div className="d-grid">
                <Button onClick={(e) => changeState()}>Search for Stop and Search</Button>
              </div>
              <div className="mb-3">

              </div>
            </Form>
        </div>
      </div>
    </div><Component2 crime={stateForceCrime.force} force={stateForceCrime.force} /></> 

   );
}

export default App;
