    {
        const data = {...stateForceCrime}
        data[e.target.id] = e.target.value
        setstateForceCrime(data)
    }

    function submit(e)
    {
        setLoadingCrimeAndForce(true);
        e.preventDefault()
        fetch(`https://data.police.uk/api/crimes-no-location?category=${stateForceCrime.crime}&force=${stateForceCrime.force}`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            setNeighbourhoods(data)
            setLoadingCrimeAndForce(false)
        })
    }

    const policeDataCrimeCategorties = () => {
        setLoadingCrime(true);
      fetch("https://data.police.uk/api/crime-categories")
        .then(response => {
          return response.json()
        })
        .then(data => {
          setCrimeCategorties(data)
          setLoadingCrime(false);
        })
    }

    const submitStopandSearch = (e) => {
        setLoadingStop(true);
        e.preventDefault()
        fetch(`https://data.police.uk/api/stops-no-location?force=${stateForceCrime.force}`)
          .then(response => {
            return response.json()
          })
          .then(data => {
            setStopSearch(data)
            setLoadingStop(false);
          })
      }


    const [force, setForce] = useState([])
    const policeForces = () => {
    setLoadingForce(true);
      fetch("https://data.police.uk/api/forces")
        .then(response => {
          return response.json()
        })
        .then(data => {
          setForce(data)
          setLoadingForce(false);
        })
    }

    useEffect(() => {
      policeDataCrimeCategorties();
      policeForces();
    }, [])       


  return (
