import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import React, { useEffect, useState } from "react"
import 'react-super-responsive-table/dist/SuperResponsiveTableStyle.css';
import Button from 'react-bootstrap/Button';
function Example(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  console.log(props.force)

  const [stateForceCrime, setstateForceCrime] = useState({force: "", crime: ""});
  const [crimeCategorties, setCrimeCategorties] = useState([])
  const [neighbourhoods, setNeighbourhoods] = useState([])
  const [stopSearch, setStopSearch] = useState([])
  const [isLoadingStop, setLoadingStop] = useState(false);
  const [isLoadingForce, setLoadingForce] = useState(false);
  const [isLoadingCrime, setLoadingCrime] = useState(false);
  const [isLoadingCrimeAndForce, setLoadingCrimeAndForce] = useState(false);

  const [state, setstate] = useState({data:""})

  const changeState = () => {  
      setstate({data:`state/props of parent component 
      is send by onClick event to another component`}); 
     }; 


  const submitStopandSearch = (e) => {
    setLoadingStop(true);
    e.preventDefault()
    fetch(`https://data.police.uk/api/stops-no-location?force=${stateForceCrime.force}`)
      .then(response => {
        return response.json()
      })
      .then(data => {
        setStopSearch(data)
        setLoadingStop(false);
      })
  }

  function submit(e)
  {
      setLoadingCrimeAndForce(true);
      e.preventDefault()
      fetch(`https://data.police.uk/api/crimes-no-location?category=${stateForceCrime.crime}&force=${stateForceCrime.force}`)
      .then(response => {
          return response.json()
      })
      .then(data => {
          setNeighbourhoods(data)
          setLoadingCrimeAndForce(false)
      })
  }



  function handle(e)
  {
      const data = {...stateForceCrime}
      data[e.target.id] = e.target.value
      setstateForceCrime(data)
  }

  const policeDataCrimeCategorties = () => {
      setLoadingCrime(true);
    fetch("https://data.police.uk/api/crime-categories")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setCrimeCategorties(data)
        setLoadingCrime(false);
      })
  }

  const [force, setForce] = useState([])
  const policeForces = () => {
  setLoadingForce(true);
    fetch("https://data.police.uk/api/forces")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setForce(data)
        setLoadingForce(false);
      })
  }

  useEffect(() => {
    policeDataCrimeCategorties();
    policeForces();
  }, [])       


  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Populate Table with Police Stats
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Crime Stats</Modal.Title>
        </Modal.Header>
        <Form onSubmit={(e) => submit(e)}>
          <div className="mb-3">
              <Form.Group>
                  <Form.Label>Select a Police Force</Form.Label>
                  <Form.Select id="force" onChange={(e) => handle(e)}>
                      <option value="Non Selected">Non Selected</option>
                      {force.map(force => (
                          <option value={force.id}>{force.id}</option>
                      ))}
                  </Form.Select>
              </Form.Group>
          </div>
          <div className="mb-3">
              <Form.Group>
                  <Form.Label>Select a Crime</Form.Label>
                  <Form.Select id="crime" onChange={(e) => handle(e)}>
                      <option value="Non Selected">Non Selected</option>
                      {crimeCategorties.map(crimeCategorties => (
                          <option value={crimeCategorties.url}>{crimeCategorties.url}</option>
                      ))}
                  </Form.Select>
              </Form.Group>
          </div>
          <div className="d-grid">
              <Button type="submit">Search for Specific Crimes in Locations</Button>
          </div>
          </Form>

          <Form onSubmit={(e) => onClick={changeState}}>
          <div className="mb-3">
              <Form.Group>
                  <Form.Label>Select a Police Force for stop and Search</Form.Label>
                  <Form.Select id="force" onChange={(e) => handle(e)}>
                      <option value="Non Selected">Non Selected</option>

                      {force.map(force => (
                          <option value={force.id}>{force.id}</option>
                      ))}
                  </Form.Select>
              </Form.Group>
          </div>
          <div className="d-grid">
              <Button type="submit">Search for Stop and Search</Button>
          </div>
          <div className="mb-3">

          </div>
      </Form>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}


export default Example;
